mapper.select:
SELECT 

FROM mem_rank a

WHERE a.id = #{id}

SELECT
FROM mem_rank a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date

SELECT 

FROM mem_rank a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mem_rank SET name = #{name}, amount = #{amount},
is_special = #{isSpecial},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks} WHERE id = #{id}

UPDATE mem_rank SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
