mapper.select:
SELECT 

FROM withdrawal_apply_for a

WHERE a.id = #{id}

SELECT 

FROM withdrawal_apply_for a



		AND a.member_id = #{memberId} 


		AND a.is_default = #{isDefault} 


		AND a.apply_username LIKE 
		'%'||#{applyUsername}||'%'
		'%'+#{applyUsername}+'%'
		concat('%',#{applyUsername},'%')


		AND a.apply_phone = #{applyPhone}


		AND a.apply_status = #{applyStatus}


		AND a.apply_date >= #{startDate}


		AND a.apply_date <= #{endDate}


		AND a.apply_date >= #{startDate} AND a.apply_date <= #{endDate}


		AND a.flag = ${flag}

 ORDER BY a.apply_date DESC

SELECT 

FROM withdrawal_apply_for a






		ORDER BY ${page.orderBy}
