mapper.select:
SELECT 

FROM brand a

WHERE a.id = #{id}

SELECT 

FROM brand a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.merchant_no = #{merchantNo}


		AND a.product_category_id = #{productCategoryId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM brand a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE brand SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
