mapper.select:
SELECT 

FROM supplier a

WHERE a.id = #{id}

SELECT
FROM supplier a
a.del_flag = #{DEL_FLAG_NORMAL}
a.company_name LIKE '%'||#{companyName}||'%' '%'+#{companyName}+'%' concat('%',#{companyName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date desc

SELECT 

FROM supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE supplier SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
